import sys
import math
from functools import lru_cache

MOD = 10**9 + 7

def main():
    import sys
    input = sys.stdin.read
    n, m, g = map(int, input().split())

    total_length = n + m

    @lru_cache(maxsize=None)
    def count_ways(length, zeros, ones):
        if length == 1:
            if (zeros == n and ones == m) or (zeros == m and ones == n):
                return 1
            return 0
        res = 0
        if zeros >= 2:
            res += count_ways(length - 1, zeros - 2, ones + 1)
        if ones >= 2:
            res += count_ways(length - 1, zeros + 1, ones - 2)
        return res % MOD

    result = count_ways(total_length, n, m)
    print(result)

if __name__ == "__main__":
    main()
