import sys

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    a = list(map(int, data[1:]))

    if n == 2:
        print(0)
        return

    a.sort()

    min_instability = float('inf')

    for i in range(n):
        if i == 0:
            current_instability = a[-2] - a[1]
        elif i == n-1:
            current_instability = a[-3] - a[0]
        else:
            current_instability = a[-1] - a[i+1]

        min_instability = min(min_instability, current_instability)

    print(min_instability)

if __name__ == "__main__":
    main()
