def main():
    s = input().strip()
    n = len(s)
    count0 = s.count('0')
    count1 = s.count('1')
    count2 = s.count('2')

    res = []
    i = 0
    while i < n and count0 > 0:
        if s[i] == '0':
            res.append('0')
            count0 -= 1
        else:
            break
        i += 1

    while i < n and count0 > 0:
        if s[i] == '1':
            res.append('0')
            count0 -= 1
        elif s[i] == '0':
