        i += 1

    while i < n and count0 > 0:
            break

    while i < n and count1 > 0:
        if s[i] == '1':
            res.append('1')
            count1 -= 1
        else:
            break
        i += 1

    while i < n and count1 > 0:
        if s[i] == '2':
            res.append('1')
            count1 -= 1
        elif s[i] == '1':
            pass
        else:
            break
        i += 1

    while i < n and count1 > 0:
            break

    while i < n and count2 > 0:
        if s[i] == '2':
            res.append('2')
            count2 -= 1
        else:
            break
        i += 1

    while i < n and count2 > 0:
        if s[i] == '1':
            res.append('2')
            count2 -= 1
        elif s[i] == '2':
            pass
        else:
            break
        i += 1

    print(''.join(res))

if __name__ == '__main__':
    main()
