import sys
import heapq

def main():
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    x = int(data[1])
    a = list(map(int, data[2:n+2]))
    b = list(map(int, data[n+2:2*n+2]))

    # We need to find the best and worst possible rank for Vasya
    # Best rank is when his total is as high as possible
    # Worst rank is when his total is as low as possible (but at least x)

    # For best rank, we try to maximize his total
    max_total = -1
    for i in range(n):
        for j in range(n):
            total = a[i] + b[j]
            if total >= x:
                max_total = max(max_total, total)

    # For worst rank, we try to minimize his total (but still >= x)
    min_total = float('inf')
    for i in range(n):
        for j in range(n):
            total = a[i] + b[j]
            if total >= x:
                min_total = min(min_total, total)

    # Now compute best and worst possible ranks

    # Best rank: assume Vasya has max_total
    # Count how many participants can have a total > max_total
    count_best = 0
    for i in range(n):
        for j in range(n):
            if a[i] + b[j] > max_total:
                count_best += 1
    best_rank = count_best + 1

    # Worst rank: assume Vasya has min_total
    # Count how many participants can have a total >= min_total
    count_worst = 0
    for i in range(n):
        for j in range(n):
            if a[i] + b[j] >= min_total:
                count_worst += 1
    worst_rank = count_worst

    print(best_rank, worst_rank)

if __name__ == "__main__":
    main()
