import sys

def solve():
    n, m = map(int, input().split())
    a = list(map(int, input().split()))

    last = 0
    ans = 0
    for i in range(m):
        if a[i] > last:
            ans += a[i] - last
        else:
            ans += n - last + a[i]
        last = a[i]

    ans += n - last
    print(ans)

if __name__ == "__main__":
    t = 1
    while t > 0:
        solve()
        t -= 1
