def solve():
    n, m = map(int, input().split())
    a = list(map(int, input().split()))

    max_val = 0
    for i in range(m):
        max_val = max(max_val, a[i])

    visited = [False] * (n + 1)
    time = 0

    for i in range(1, max_val + 1):
        if visited[i]:
            continue

        j = i
        while not visited[j]:
            visited[j] = True
            j = a[j - 1]

        time += j - i

    print(time)

if __name__ == "__main__":
    t = 1  # int(input())
    for _ in range(t):
        solve()
