def is_major(triad):
    """Check if a triad is major."""
    notes = ['C', 'C#', 'D', 'D#', 'E', 'F', 'F#', 'G', 'G#', 'A', 'B', 'H']
    x, y, z = map(notes.index, triad)

    # Calculate the distances between notes in a cyclic manner
    dist_xy = (y - x) % 12
    dist_yz = (z - y) % 12

    return dist_xy == 4 and dist_yz == 3


def is_minor(triad):
    """Check if a triad is minor."""
    notes = ['C', 'C#', 'D', 'D#', 'E', 'F', 'F#', 'G', 'G#', 'A', 'B', 'H']
    x, y, z = map(notes.index, triad)

    # Calculate the distances between notes in a cyclic manner
    dist_xy = (y - x) % 12
    dist_yz = (z - y) % 12

    return dist_xy == 3 and dist_yz == 4


