def check_mentsu(hand):
    # Sort the hand by suit and then by number
    hand.sort(key=lambda x: (x[0], int(x[1])))

    # Check for koutsu
    suits = {}
    for tile in hand:
        if tile[1] not in suits:
            suits[tile[1]] = []
        suits[tile[1]].append(int(tile[0]))
    for suit in suits.values():
        if len(suit) == 3 and len(set(suit)) == 1:
            return True

    # Check for shuntsu
    for i in range(len(hand) - 2):
        if hand[i][1] == hand[i+1][1] == hand[i+2][1] and \
           hand[i][0] + 1 == int(hand[i+1][0]) and \
           int(hand[i+1][0]) + 1 == int(hand[i+2][0]):
            return True

    return False

def solve(hand):
    if check_mentsu(hand):
        return 0
    else:
        return 1

