def solve(n, pos, l, r):
    if l > pos or r < pos:
        return n - 1
    elif l <= 1 and r >= n:
        return 0
    else:
        return min(max(l - 1, 1), pos - 1) + min(n - r, pos + 1) - 1

n, pos, l, r = map(int, input().split())
print(solve(n, pos, l, r))

