def vasily_the_bear():
    mod = 10**9 + 7
    n, m, g = map(int, input().split())

    if (n > 0 and m > 0 and g == 1) or (n == 0 and m == 0 and g == 1):
        return 0

    result = 0
    for k in range(min(n, m) + 1):
        result += comb(n - k, m - k, mod)
        result %= mod

    return result


def comb(n, m, mod):
    if m > n:
        return 0
    denominator = 1
    for i in range(1, m + 1):
        denominator *= i
        denominator %= mod
    numerator = 1
    for i in range(1, n - m + 1):
        numerator *= i
        numerator %= mod
    return (numerator * pow(denominator, mod - 2, mod)) % mod


if __name__ == "__main__":
    t = 1
    for _ in range(t):
        print(vasily_the_bear())
