def solve():
    MOD = 10**9 + 7
    n, m, g = map(int, input().split())

    if (n - m) * g > 0:
        return "0"

    ans = 0
    for i in range(min(n, m) + 1):
        if (n - i) * g == (m - i) * (1 - g):
            ans += 1

    return str(ans % MOD)


