def check_x(n, grid):
    # Check if the first diagonal is 'X'
    diagonal1 = all(row[i] == grid[0][0] for i, row in enumerate(grid))
    diagonal2 = all(row[n - i - 1] == grid[0][0] for i, row in enumerate(grid))

    # Check if both diagonals are 'X'
    if not (diagonal1 and diagonal2):
        return False

    # Check if the rest of the grid is filled with a different character
    for i in range(1, n - 1):
        for j in range(1, n - 1):
            if grid[i][j] != grid[0][0]:
                return False

    # Check if the corners are 'X'
    for i in range(1, n - 1):
        if grid[i][i] != grid[0][0] or grid[i][n - i - 1] != grid[0][0]:
            return False

    return True


n = int(input())
grid = [list(input().strip()) for _ in range(n)]

result = 'YES' if check_x(n, grid) else 'NO'
print(result)
