def solve():
    n = int(input())
    A = input()
    dp = [[0] * (n + 1) for _ in range(2)]
    moves = [[[''] * (n + 1) for _ in range(2)] for _ in range(n + 1)]

    for i in range(1, 2 * n + 1):
        digit = int(A[i - 1])
        for j in range(1, n + 1):
            max_val = max(dp[0][j - 1] + digit, dp[1][j - 1] + digit)
            if max_val > dp[0][j]:
                dp[0][j] = max_val
                moves[j][0] = moves[j - 1][0] + 'H'
            if max_val > dp[1][j]:
                dp[1][j] = max_val
                moves[j][1] = moves[j - 1][1] + 'M'

    print(moves[n][0][:n] + moves[n][1][n:])

if __name__ == "__main__":
    t = 1
    for _ in range(t):
        solve()
