import sys

def array_stabilization(n, a):
    a.sort()
    max_instability = a[n - 1] - a[0]
    min_instability = float('inf')

    for i in range(1, n - 1):
        instability = max(a[i] - a[0], a[n - 1] - a[i])
        if instability < max_instability:
            max_instability = instability
        min_instability = min(min_instability, instability)

    return min(max_instability, min_instability)

def main():
    lines = sys.stdin.read().splitlines()
    n = int(lines[0])
    a = list(map(int, lines[1].split()))
    print(array_stabilization(n, a))

if __name__ == "__main__":
    main()
