import math

def solve():
    n = int(input())
    a = list(map(int, input().split()))

    # Sort the array in descending order
    a.sort(reverse=True)

    b = [0] * n
    used = [False] * n

    for i in range(n):
        if i % 2 == 0:
            b[i] = a[i]
            used[i] = True

    for i in range(n):
        if not used[i]:
            j = i - 1
            while j >= 0 and used[j]:
                j -= 1
            k = i + 1
            while k < n and used[k]:
                k += 1
            b[i] = a[j]
            used[j] = True
            b[k] = a[i]
            used[i] = True

    print(*b)


if __name__ == "__main__":
    t = 1
    while t > 0:
        solve()
        t -= 1
