import math

def solve():
    p = int(input())

    n = 0
    while True:
        pairs = n * (n - 1) // 2
        if pairs >= p:
            break
        n += 1

    if n == 1:
        print("1 0")
        return

    ans = n + (p - n * (n - 1) // 2)
    unidirectional_pairs = 0
    for i in range(2, n):
        unidirectional_pairs += (i - 2) * (n - i)

    print(ans, unidirectional_pairs)

if __name__ == "__main__":
    solve()
