import math

def solve():
    p = int(input())

    if p == 0:
        print(0, 0)
        return

    n = 1
    while True:
        pairs = n * (n - 1) // 2
        if pairs >= p:
            break
        n += 1

    ans_n = n
    ans_uni = 0
    for i in range(2, n):
        pairs = i * (i - 1) // 2
        if pairs > p:
            continue
        pairs -= (n - i) * (n - i - 1) // 2
        ans_uni += pairs

    print(ans_n, ans_uni)

if __name__ == "__main__":
    t = 1
    # t = int(input())
    for _ in range(t):
        solve()
