    return n, l, a

def find_min_radius(n, l, a):
    a.sort()
    max_left = [0] * n
    max_right = [0] * n

    for i in range(1, n):
        max_left[i] = max(max_left[i-1], a[i-1])

    for i in range(n-2, -1, -1):
        max_right[i] = max(max_right[i+1], a[i+1])

    min_radius = 0.0
    for i in range(n):
