import sys

def read_input():
    return map(int, input().split())

def main():
    n, mod = read_input()

    # Initialize dp and inv arrays
    dp = [[0] * (n + 1) for _ in range(n + 1)]
    inv = [[0] * (n + 1) for _ in range(n + 1)]

    # Calculate binomial coefficients
    for i in range(n + 1):
        dp[i][0] = 1
        for j in range(1, min(i + 1, n // 2 + 1)):
            dp[i][j] = (dp[i - 1][j - 1] + dp[i - 1][j]) % mod
        if i >= n // 2:
            for j in range(n // 2 + 1, i + 1):
                dp[i][j] = (dp[i - 1][i - j] + dp[i - 1][j - 1]) % mod

    # Calculate inversion counts
    for i in range(n + 1):
        for j in range(1, i + 1):
            inv[j][i] = (inv[j][i - 1] + (i - j + 1) * (j > 1)) % mod
        for j in range(1, i):
            inv[i][j] = (inv[i - 1][j] + inv[i][j - 1] - inv[i - 1][j - 1] + mod) % mod

    # Calculate final answer
    ans = 0
    for k in range(1, n + 1):
        for i in range(k + 1):
            for j in range(i + 1):
                if i + j < k:
