def read_input():
    n, mod = map(int, input().split())
    return n, mod

def calculate_permutations(n, mod):
    permutations = [[0] * (n + 1) for _ in range(n + 1)]
    permutations[0][0] = 1

    for i in range(1, n + 1):
        for j in range(i + 1):
            permutations[i][j] = permutations[i - 1][j]
            if j > 0:
                permutations[i][j] += permutations[i - 1][j - 1]
                permutations[i][j] %= mod

    return permutations

def calculate_inversions(n, mod):
    inversions = [[0] * (n + 1) for _ in range(n + 1)]
    for i in range(1, n + 1):
        inversions[i][i] = 1
        for j in range(i - 1, 0, -1):
            inversions[i][j] = inversions[i - 1][j] + inversions[i - 1][j + 1]

    return inversions

def calculate_pairs(n, mod):
    permutations = calculate_permutations(n, mod)
    inversions = calculate_inversions(n, mod)

    pairs = 0
    for k in range(n // 2 + 1):
        for i in range(k + 1):
            for j in range(k + 1):
                if i + j < k:
                    continue
                pair_count = permutations[n - k - 1][i] * permutations[k - i][j] % mod
                pair_count *= inversions[n - k - 1][k - i - j]
                pair_count %= mod
                pairs += pair_count

