def mod_inverse(a, m):
    """Calculate the modular inverse of a number."""
    def extended_gcd(a, b):
        if a == 0:
            return b, 0, 1
        else:
            gcd, x, y = extended_gcd(b % a, a)
            return gcd, y - (b // a) * x, x

    gcd, x, _ = extended_gcd(a, m)
    if gcd != 1:
        return None
    else:
        return x % m

def calculate_answer(n):
    """Calculate the sum of d(1,n) for all good trees."""
    MOD = 998244353
    # Calculate the product of factorials.
    factorial_product = 1
    for i in range(2, n):
        factorial_product *= i
        factorial_product %= MOD
    # Calculate the inverse of the product of factorials.
    factorial_inverse_product = mod_inverse(factorial_product, MOD)
    # Calculate the answer.
    answer = ((n - 1) * factorial_inverse_product) % MOD
    return answer

def main():
    """Read input from standard input and print the answer to standard output."""
    n = int(input())
    answer = calculate_answer(n)
    print(answer)

if __name__ == "__main__":
    main()
