def new_year_candles():
    a, b = map(int, input().split())

    hours = 0
    candles = a

    while True:
        hours += 1
        if candles >= b:
            new_candles = candles // b
            candles = (new_candles + 1) * b - new_candles * (b - 1)
        else:
            break

    print(hours)

if __name__ == "__main__":
    new_year_candles()
