def new_year_candles():
    a, b = map(int, input().split())

    hours = 0
    while a >= b:
        hours += 1
        a += a // b
        a -= a % b

    hours += a // b
    return str(hours)


if __name__ == "__main__":
    t = 1
    for _ in range(t):
        print(new_year_candles())
