def new_year_candles():
    a, b = map(int, input().split())

    hours = 0
    candles = a

    while True:
        hours += 1
        if candles == 0:
            break
        elif candles >= b:
            new_candles = candles // b
            hours += new_candles * (b + 1)
            candles = new_candles + candles % b
        else:
            hours += candles
            candles = 0

    return str(hours - 1)


