import sys

def orientation(p, q, r):
    """Calculate the orientation of three points in the plane."""
    val = (q[1] - p[1]) * (r[0] - q[0]) - (q[0] - p[0]) * (r[1] - q[1])
    if val == 0:
        return 0  # Collinear
    return 1 if val > 0 else 2  # Clockwise or counterclockwise

def convex_hull(points):
    """Compute the convex hull of a set of 2D points."""
    n = len(points)
    if n < 3:
        return points

    hull = []
    l = 0
    for i in range(1, n):
        if points[i][0] < points[l][0]:
            l = i

    p = l
    while True:
        hull.append(points[p])
        q = (p + 1) % n

        for i in range(n):
            if orientation(points[p], points[i], points[q]) == 2:
                q = i

        p = q
        if p == l:
            break

    return hull

def can_assign_rebels(rebels, bases):
    """Check if it's possible to assign rebels to bases without any collisions."""
    combined_points = rebels + bases
    hull = convex_hull(combined_points)

    # If the number of points in the convex hull is equal to the number of bases,
    # then we can assign rebels to bases without any collisions.
    return len(hull) // 2 == len(bases)

