import sys

def orientation(p, q, r):
    """Calculate the orientation of three points in the plane."""
    val = (q[1] - p[1]) * (r[0] - q[0]) - (q[0] - p[0]) * (r[1] - q[1])
    if val == 0:
        return 0  # Collinear
    return 1 if val > 0 else 2  # Clockwise or counterclockwise

def convex_hull(points):
    """Compute the convex hull of a set of 2D points."""
    n = len(points)
    if n < 3:
        raise ValueError("Convex hull not possible")

    # Find the leftmost point
    leftmost = min(points, key=lambda p: (p[0], p[1]))
    points.remove(leftmost)
    points.append((leftmost[0], float('-inf')))  # Mark as visited

    hull = []
    while True:
        hull.append(leftmost)
        p = None
        for q in points:
            if p is None or orientation(leftmost, p, q) == 2:
                p = q
        if p == (leftmost[0], float('-inf')):
            break
        leftmost = p

    hull.pop()  # Remove the last point marked as visited
    return hull

