import sys

def read_input():
    n = int(input())
    left = input()
    right = input()
    return n, left, right

def solve(n, left, right):
    pairs = []
    def dfs(i, j, left, right, pairs):
        if i == n:
            return
        if j == n:
            dfs(i + 1, 0, left, right, pairs)
            return
        if left[i] != '?' and right[j] != '?' and left[i] != right[j]:
            return
        if left[i] == '?' or right[j] == '?':
            pairs.append((i + 1, j + 1))
            dfs(i, j + 1, left, right, pairs)
            return
        if left[i] == right[j]:
            pairs.append((i + 1, j + 1))
            dfs(i, j + 1, left, right, pairs)
            return
        dfs(i, j + 1, left, right, pairs)

    dfs(-1, -1, left, right, pairs)
    pairs.sort()
    output = []
    for i in range(len(pairs)):
        output.append(f'{pairs[i][0]} {pairs[i][1]}')
    return len(pairs), '\n'.join(output)

if __name__ == "__main__":
    t = 1
    for _ in range(t):
        n, left, right = read_input()
        k, output = solve(n, left, right)
        print(k)
        print(output)
