import sys

def solve():
    n, m = map(int, input().split())
    souvenirs = []
    for _ in range(n):
        w, c = map(int, input().split())
        souvenirs.append((w, c))

    souvenirs.sort(key=lambda x: x[1] / x[0], reverse=True)

    max_cost = 0
    remaining_weight = m

    for weight, cost in souvenirs:
        if remaining_weight >= weight:
            max_cost += cost
            remaining_weight -= weight
        else:
            max_cost += (remaining_weight / weight) * cost
            break

    print(int(max_cost))

if __name__ == "__main__":
    t = 1
    for _ in range(t):
        solve()
