import sys

def solve(n, m, souvenirs):
    # Sort the souvenirs by weight-to-cost ratio in descending order
    souvenirs.sort(key=lambda x: (x[0], -x[1]))

    dp = [[0, 0]] * (m + 1)
    for w, c in souvenirs:
        for i in range(m, w - 1, -1):
            if dp[i][0] < dp[i - w][0] + c:
                dp[i] = [dp[i - w][0] + c, i]
    max_cost = max(dp[m][0], dp[m][1])
    return max_cost

def main():
    input_lines = sys.stdin.readlines()
    n_m = list(map(int, input_lines[0].strip().split()))
    souvenirs = []
    for i in range(1, len(input_lines)):
        w_c = list(map(int, input_lines[i].strip().split()))
        souvenirs.append(w_c)

    outputs = []
    for i in range(len(n_m)):
        n, m = n_m[i]
        outputs.append(str(solve(n, m, souvenirs[i*n:(i+1)*n])))

    print('\n'.join(outputs))

if __name__ == "__main__":
    main()
