import sys

def solve():
    S = sys.stdin.readline().strip()
    n = len(S)

    count_a = 0
    count_b = 0
    count_c = 0

    state = 0 # 0: looking for 'a', 1: looking for 'b', 2: looking for 'c'

    for char in S:
        if state == 0:
            if char == 'a':
                count_a += 1
            elif char == 'b':
                state = 1
                count_b += 1
            else: # char == 'c'
                print("NO")
                return
        elif state == 1:
            if char == 'b':
                count_b += 1
            elif char == 'c':
                state = 2
                count_c += 1
            else: # char == 'a'
                print("NO")
                return
        else: # state == 2
            if char == 'c':
                count_c += 1
            else: # char == 'a' or 'b'
                print("NO")
                return

    if count_a == 0 or count_b == 0:
        print("NO")
        return

    if count_c == count_a or count_c == count_b:
        print("YES")
    else:
        print("NO")

if __name__ == '__main__':
    solve()
