import sys

sys.setrecursionlimit(2 * 10**5 + 500)
input = sys.stdin.readline
n, m = map(int, input().split())
adj = [[] for i in range(n + 1)]
for i in range(m):
  a, b, c = map(int, input().split())
  adj[a].append((b, c, i))
  adj[b].append((a, c, i))
ans = [0] * m
vis = [False] * (n + 1)
parity = [0] * (n + 1)


def dfs(u):
  vis[u] = True
  for v, c, idx in adj[u]:
    if not vis[v]:
      dfs(v)
      if parity[v] == 1:
        ans[idx] = 1 if u < v else 0
        parity[u] ^= 1
      else:
        ans[idx] = 0 if u < v else 1
        parity[v] ^= 1
    elif v != n:
      if parity[v] == 1:
        ans[idx] = 1 if u < v else 0
        parity[u] ^= 1
      else:
        ans[idx] = 0 if u < v else 1
        parity[v] ^= 1


dfs(1)
for x in ans:
  print(x)
