import sys, re

text = sys.stdin.read()
if not text:
    sys.exit(0)

notes_list = re.findall(r"C#|D#|F#|G#|[A-H]", text)

order = {
    "C":0, "C#":1, "D":2, "D#":3, "E":4, "F":5, "F#":6,
    "G":7, "G#":8, "A":9, "B":10, "H":11
}

def classify(triple):
    a, b, c = triple
    vals = [order[a], order[b], order[c]]
    from itertools import permutations
    for x,y,z in permutations(vals, 3):
        if (y - x) % 12 == 4 and (z - y) % 12 == 3:
            return "major"
        if (y - x) % 12 == 3 and (z - y) % 12 == 4:
            return "minor"
    return "strange"

out = []
for i in range(0, len(notes_list), 3):
    trio = notes_list[i:i+3]
    if len(trio) < 3:
        break
    out.append(classify(trio))

print("\n".join(out))

