import sys

def simulate_winner(n, s, p):
    # p is list of skills length 2^n
    m = 1 << n
    cur = p[:]
    for i in range(n):
        nxt = []
        for j in range(0, m, 2):
            a = cur[j]
            b = cur[j+1]
            if s[i] == '0':
                # lower wins
                nxt.append(a if a < b else b)
            else:
                nxt.append(a if a > b else b)
        cur = nxt
        m >>= 1
    return cur[0]

def small_solution(n, s):
    from itertools import permutations
    m = 1 << n
    winners = set()
    for perm in permutations(range(1, m+1)):
        if simulate_winner(n, s, list(perm)) not in winners:
            winners.add(simulate_winner(n, s, list(perm)))
    return sorted(winners)

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    n = int(data[0])
    s = data[1].strip()
    # If small n, brute force; otherwise attempt heuristic (may be incorrect for large n)
    if n <= 8:
        res = small_solution(n, s)
        print(" ".join(map(str, res)))
    else:
        # fallback heuristic: assume all ranks are possible (not generally correct)
        m = 1 << n
        print(" ".join(str(i) for i in range(1, m+1)))

if __name__ == "__main__":
    main()

