if not data:
    sys.exit(0)
ptr = 0
n = int(data[ptr].strip()); ptr += 1
m = 2 * n - 2
arr = []
for i in range(m):
    arr.append(data[ptr].strip()); ptr += 1

# find the two strings of length n-1
cand = [s for s in arr if len(s) == n-1]
a, b = cand[0], cand[1]

def try_candidate(s):
    # prefix for length l: s[:l], suffix: s[n-l:]
    used_pref = {l: False for l in range(1, n)}
    used_suf = {l: False for l in range(1, n)}
    res = []
    for t in arr:
        l = len(t)
        is_pref = (s[:l] == t)
        is_suf = (s[n-l:] == t)
        if is_pref and not used_pref[l]:
            res.append('P')
            used_pref[l] = True
        elif is_suf and not used_suf[l]:
            res.append('S')
            used_suf[l] = True
        else:
            # if t matches both but pref already used, try suffix if free
            if is_pref and not used_suf[l]:
                # This branch happens if pref used but suffix free and strings equal for both sides;
                # but above would have caught is_pref and not used_pref; here pref used so try suf
                res.append('S')
                used_suf[l] = True
            elif is_suf and not used_pref[l]:
                res.append('P')
                used_pref[l] = True
            else:
                return None
    return ''.join(res)

s1 = a + b[-1]
s2 = b + a[-1]

ans = try_candidate(s1)
if ans is None:
    ans = try_candidate(s2)
if ans is None:
    # theoretically shouldn't happen
    ans = 'P' * m
print(ans)

