import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)

def solve_triplet(tokens):
    tiles = tokens
    nums = []
    suits = []
    for t in tiles:
        nums.append(int(t[0]))
        suits.append(t[1])
    # check koutsu
    if tiles[0] == tiles[1] == tiles[2]:
        return 0
    # check shuntsu: same suit and numbers are consecutive (in any order)
    for i in range(3):
        for j in range(i+1,3):
            for k in range(j+1,3):
                if suits[i]==suits[j]==suits[k]:
                    arr = sorted([nums[i],nums[j],nums[k]])
                    if arr[0]+1==arr[1] and arr[1]+1==arr[2]:
                        return 0
    # check if can get with one draw
    # if any two identical -> draw same to make triplet
    for i in range(3):
        for j in range(i+1,3):
            if tiles[i]==tiles[j]:
                return 1
    # if any two same suit and numbers difference <=2 -> can draw the third
    for i in range(3):
        for j in range(i+1,3):
            if suits[i]==suits[j]:
                if abs(nums[i]-nums[j])<=2:
                    return 1
    return 2

outs = []
if len(data) % 3 == 0:
    for i in range(0, len(data), 3):
        outs.append(str(solve_triplet(data[i:i+3])))
else:
    # fallback: use first three tokens
    outs.append(str(solve_triplet(data[:3])))

sys.stdout.write("\n".join(outs))

