data = list(map(int, sys.stdin.read().split()))
if not data:
    sys.exit(0)
outs = []
if len(data) % 4 != 0:
    data = data[:4]
for i in range(0, len(data), 4):
    n, pos, l, r = data[i:i+4]
    if l == 1 and r == n:
        outs.append("0")
    elif l == 1:
        outs.append(str(abs(pos - r) + 1))
    elif r == n:
        outs.append(str(abs(pos - l) + 1))
    else:
        outs.append(str(min(abs(pos - l), abs(pos - r)) + (r - l) + 2))
sys.stdout.write("\n".join(outs))

