    pref1[i] = pref1[i-1] + (1 if a[i]==1 else 0)
suf2 = [0]*(n+2)
for i in range(n, 0, -1):
    suf2[i] = suf2[i+1] + (1 if a[i]==2 else 0)

ans = 0
# try every l as segment start
for l in range(1, n+1):
    cur = 0
    cnt2 = 0
    # extend r
    for r in range(l, n+1):
        if a[r] == 1:
            # add a '1' to ones-part inside segment
            cur += 1
        else:
            # add a '2' to twos-part inside segment
            cnt2 += 1
        # cur should represent best non-decreasing subsequence inside reversed segment:
        # either keep existing ones-then-twos value (cur) or switch to taking more twos first (cnt2)
        if cnt2 > cur:
            cur = cnt2
        total = pref1[l-1] + cur + suf2[r+1]
        if total > ans:
            ans = total

# also consider no reversal (segment empty) — already covered when l>r is not iterated;
# but include full array case of taking pref ones + suf twos:
for i in range(0, n+1):
    cand = pref1[i] + suf2[i+1]
    if cand > ans:
        ans = cand

print(ans)

