import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it))
a = [0] * (n + 1)
for i in range(1, n + 1):
    try:
        a[i] = int(next(it))
    except StopIteration:
        a[i] = 0

ones = [0] * (n + 1)
for i in range(1, n + 1):
    ones[i] = ones[i - 1] + (1 if a[i] == 1 else 0)
total_ones = ones[n]
total_twos = n - total_ones

# original max over k of (2*ones_prefix[k] - k)
orig_max = -10**9
for k in range(0, n + 1):
    val = 2 * ones[k] - k
    if val > orig_max:
        orig_max = val

ans = total_twos + orig_max

# iterate r and compute contributions
for r in range(1, n + 1):
    ones_r = ones[r]
    # S[t] = t + 2*ones_in[t,r] for t=1..r
    # build suffix max of S
    S = [ -10**9 ] * (r + 2)
    for t in range(1, r + 1):
        S[t] = t + 2 * (ones_r - ones[t - 1])
    sufmax = [ -10**9 ] * (r + 3)
    for t in range(r, 0, -1):
        v = S[t]
        if v >= sufmax[t + 1]:
            sufmax[t] = v
        else:
            sufmax[t] = sufmax[t + 1]
    for l in range(1, r + 1):
        val = 2 * ones[l - 1] - (l + r) + sufmax[l]
        mid = orig_max if orig_max >= val else val
        cand = total_twos + mid
        if cand > ans:
            ans = cand

print(ans)
