#!/usr/bin/env python3
import sys

MOD = 1000000007

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
n = int(data[0]); m = int(data[1]); g = int(data[2])

if m == 0:
    # Only one string: all zeros
    ans = 1 if ((n - 1) % 2 == g) else 0
    print(ans % MOD)
    sys.exit(0)

N = n + m
fact = [1] * (N + 1)
for i in range(1, N + 1):
    fact[i] = fact[i-1] * i % MOD
invfact = [1] * (N + 1)
invfact[N] = pow(fact[N], MOD-2, MOD)
for i in range(N, 0, -1):
    invfact[i-1] = invfact[i] * i % MOD

def comb(a, b):
    if b < 0 or b > a or a < 0:
        return 0
    return fact[a] * invfact[b] % MOD * invfact[a-b] % MOD

ans = 0
if m >= 2:
    for p in range(0, n+1):
        if (p % 2) == g:
            r = n + m - p - 1
            ans = (ans + comb(r, m-1)) % MOD
else:
    # m == 1
    for p in range(0, n):
        if (p % 2) == g:
            r = n + m - p - 1
            ans = (ans + comb(r, m-1)) % MOD
    p = n
    h_at_p = 1 if (p % 2 == 0) else 0
    if h_at_p == g:
        r = n + m - p - 1
        ans = (ans + comb(r, m-1)) % MOD

print(ans % MOD)

