    print(1 if final_val == g else 0)
    sys.exit(0)

N = n + m + 5
fact = [1] * (N)
invfact = [1] * (N)
for i in range(1, N):
    fact[i] = fact[i-1] * i % MOD
invfact[N-1] = pow(fact[N-1], MOD-2, MOD)
for i in range(N-2, -1, -1):
    invfact[i] = invfact[i+1] * (i+1) % MOD

def C(a, b):
    if b < 0 or b > a or a < 0:
        return 0
    return fact[a] * invfact[b] % MOD * invfact[a-b] % MOD

ans = 0
r = (n - g) & 1
if m == 1:
    for k in range(1, n+1):
        if (k & 1) == r:
            ans = (ans + 1) % MOD
    if g == (1 ^ (n & 1)):
        ans = (ans + 1) % MOD
else:
    k = r
    while k <= n:
        ans = (ans + C(m + k - 1, k)) % MOD
        k += 2

print(ans % MOD)

