for i in range(1, maxN + 1):
    fact[i] = fact[i-1] * i % MOD
invfact = [1] * (maxN + 1)
invfact[maxN] = pow(fact[maxN], MOD-2, MOD)
for i in range(maxN, 0, -1):
    invfact[i-1] = invfact[i] * i % MOD

def comb(n, k):
    if k < 0 or k > n or n < 0:
        return 0
    return fact[n] * invfact[k] % MOD * invfact[n-k] % MOD

out_lines = []
for n, m, g in queries:
    total = comb(n+m, n)
    if n == 0:
        A = 1 if m == 1 else 0
    else:
        S = 0
        # sum_{i=0}^{n-1} (-1)^i C(m + n-1 - i, m)
        for i in range(0, n):
            term = comb(m + n - 1 - i, m)
            if (i & 1) == 0:
                S += term
            else:
