def process_case(n, grid):
    diag_char = grid[0][0]
    other_char = None
    for i in range(n):
        if grid[i][i] != diag_char or grid[i][n-1-i] != diag_char:
            return "NO"
        for j in range(n):
            if j == i or j == n-1-i:
                continue
            if other_char is None:
                other_char = grid[i][j]
                if other_char == diag_char:
                    return "NO"
            else:
                if grid[i][j] != other_char:
                    return "NO"
    return "YES"

