other_char = None
ok = True

for i in range(n):
    for j in range(n):
        ch = grid[i][j]
        if i == j or i + j == n - 1:
            if diag_char is None:
                diag_char = ch
            elif ch != diag_char:
                ok = False
                break
        else:
            if other_char is None:
                other_char = ch
            elif ch != other_char:
                ok = False
                break
    if not ok:
        break

if not ok or diag_char == other_char:
    print("NO")
else:
    print("YES")

