data = sys.stdin.read().split()
if not data:
    sys.exit(0)
n = int(data[0])
rows = data[1:1+n]

diag_char = rows[0][0]
other_char = None
ok = True
for i in range(n):
    for j in range(n):
        ch = rows[i][j]
        if i == j or i + j == n - 1:
            if ch != diag_char:
                ok = False
                break
        else:
            if other_char is None:
                other_char = ch
            elif ch != other_char:
                ok = False
                break
    if not ok:
        break

if other_char is None or diag_char == other_char:
    ok = False

print("YES" if ok else "NO")

