import sys

def process_case(a, s):
    s_sorted = sorted(s, reverse=True)
    j = 0
    res = []
    for ch in a:
        if j < len(s_sorted) and s_sorted[j] > ch:
            res.append(s_sorted[j])
            j += 1
        else:
            res.append(ch)
    return ''.join(res)

