import sys

data = [line.strip() for line in sys.stdin.readlines() if line.strip()!='']
if len(data) < 2:
    sys.exit(0)
a = data[0].strip()
s = data[1].strip()

cnt = [0]*10
for ch in s:
    if ch.isdigit():
        cnt[ord(ch)-48] += 1

res = list(a)
p = 9
for i, ch in enumerate(res):
    cur = ord(ch)-48
    while p >= 0 and cnt[p] == 0:
        p -= 1
    if p > cur:
        res[i] = chr(48 + p)
        cnt[p] -= 1

sys.stdout.write(''.join(res))

