#!/usr/bin/env python3
import sys

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    # The input may contain multiple test cases in the prompt examples,
    # but per problem statement there's a single a and single s.
    # If multiple pairs are provided consecutively, process each pair.
    out_lines = []
    i = 0
    while i < len(data):
        a = data[i].strip()
        i += 1
        if i >= len(data):
            # no s given — treat s as empty
            s = ""
        else:
            s = data[i].strip()
            i += 1
        a_list = list(a)
        s_digits = sorted(s, reverse=True)
        si = 0
        for idx in range(len(a_list)):
            if si >= len(s_digits):
                break
            if s_digits[si] > a_list[idx]:
                a_list[idx] = s_digits[si]
                si += 1
            else:
                # if not greater, skip replacing this position
                continue
        out_lines.append(''.join(a_list))
    sys.stdout.write("\n".join(out_lines))

if __name__ == "__main__":
    main()
