import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
outs = []
try:
    while True:
        n = int(next(it))
        a = [int(next(it)) for _ in range(n)]
        s = next(it)
        # s[0] is bit 0 (LSB)
        prefix_lower = [0] * (n + 1)
        for i in range(1, n + 1):
            prefix_lower[i] = prefix_lower[i-1] + a[i-1]
        total = 0
        for i in range(n):
            if s[i] == '1':
                total += a[i]
        ans = total
        currentHigh = 0  # sum of a[j] for j>i where s[j]==1
        for i in range(n-1, -1, -1):
            if s[i] == '1':
                # change bit i to 0 and set all lower bits to 1
                candidate = currentHigh + prefix_lower[i]
                if candidate > ans:
                    ans = candidate
                currentHigh += a[i]
        outs.append(str(ans))
except StopIteration:
    pass

sys.stdout.write("\n".join(outs))

