import sys

def solve_case(n, l, positions):
    positions.sort()
    max_gap = 0
    for i in range(1, n):
        gap = positions[i] - positions[i-1]
        if gap > max_gap:
            max_gap = gap
    left_gap = positions[0] - 0
    right_gap = l - positions[-1]
    d = max(max_gap / 2.0, left_gap, right_gap)
    return d

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    outputs = []
    while True:
        try:
            n = int(next(it))
            l = int(next(it))
        except StopIteration:
            break
        positions = []
        for _ in range(n):
            try:
                positions.append(int(next(it)))
            except StopIteration:
                positions.append(0)
        d = solve_case(n, l, positions)
        outputs.append("{:.10f}".format(d))
    sys.stdout.write("\n".join(outputs))

if __name__ == "__main__":
    main()

